import React from 'react';
import '../styles/About.css';

const About = () => {
  const facts = [
    {
      icon: 'üó£Ô∏è',
      title: 'Languages Known',
      description: 'Tamil, English, Hindi'
    },
    {
      icon: 'üíº',
      title: 'Current Role',
      description: 'B.Tech IT Student & Backend Developer'
    },
    {
      icon: '‚ù§Ô∏è',
      title: 'Interests',
      description: 'Machine Learning, IoT Projects, Data Analytics'
    }
  ];

  return (
    <section id="about" className="section about-section">
      <div className="container">
        <h2 className="section-title">About Me</h2>
        <div className="quick-facts">
          {facts.map((fact, index) => (
            <div 
              key={index} 
              className={`fact-card fade-in-up ${index > 0 ? `delay-${index}` : ''}`}
            >
              <div className="fact-icon">{fact.icon}</div>
              <h3>{fact.title}</h3>
              <p>{fact.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default About;
